#!/bin/bash

jvm="/Users/kelthuzadx/Desktop/hack-jdk12/jdk/java"
cp_path=$(pwd)"/../target/classes/"
target_dir=$(pwd)"/../target/classes/com/kelthuzadx/yarrow/test"
target_files=$(ls $target_dir)
cd $target_dir
for file_name in $target_files
do
 javap -public $file_name|sed -E 's/Compiled from "(.*)\.java.*"/\1/g'
done

jvm_opt="-XX:+UnlockExperimentalVMOptions
-XX:+EnableJVMCI
-XX:+UseJVMCICompiler
-Djvmci.Compiler=yarrow
-Dyarrow.Debug.PrintIR=true
-Dyarrow.Debug.PrintCFG=true
-Dyarrow.Debug.PrintIRToFile=true
-Xcomp
-XX:CompileCommand=compileonly,*"

